import com.eviware.soapui.support.XmlHolder


def holder = new XmlHolder( messageExchange.responseContentAsXml )
def value = holder.getNodeValue("//*:DivideResult")

def rnd1 = Integer.valueOf(context.expand ('${#TestCase#randomInt1}'))
def rnd2 = Integer.valueOf(context.expand ('${#TestCase#randomInt2}'))

assert Integer.valueOf(value)==Math.round(rnd1/rnd2)

assert messageExchange.responseHeaders["Content-Type"] != null

